from collections import defaultdict

def find_num(str):
  d = defaultdict(int)
  for i in range(14):
    d[str[i]] += 1
  i = 0
  while True:
    # if len(d) == 4:
    if len(d) == 14:
      return i + 14
    d[str[i]] -= 1
    if d[str[i]] == 0:
      del d[str[i]]
    d[str[i+14]] += 1
    i += 1


print(find_num("bvwbjplbgvbhsrlpgdmjqwftvncz"))
print(find_num("nppdvjthqldpwncqszvftbrmjlhg"))
print(find_num("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"))
print(find_num("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"))
input = ""

