var question = "\\\\";

function day2(str) {
    const array = str.split('\n');
    let result = "";
    const pos = [1,1];
    // [x, y]
    const map = [[7,4,1],[8,5,2],[9,6,3]];
    // [0,0] => 7; [2,2] => 3;
    array.forEach(line=>{
        const ops = line.split('');
        ops.forEach(op=>{
            switch (op) {
                case 'U':
                    if (pos[1] < 2) pos[1] += 1;
                    break;
                case 'D':
                    if (pos[1] > 0) pos[1] -= 1;
                    break;
                case 'L':
                    if (pos[0] > 0) pos[0] -= 1;
                    break;
                case 'R':
                    if (pos[0] < 2) pos[0] += 1;
                    break;
                default:
                    break;
            }
        });
        const [x, y] = pos;
        result += map[x][y];
    });
    return result;
}

const assert = require('assert');
describe("day2", ()=>{
    it("should return 1985 when the input is \n'ULL\nRRDDD\nLURDL\nUUUUD'", ()=>{
        assert.equal(day2('ULL\nRRDDD\nLURDL\nUUUUD'), 1985);
    });
})

console.log("The result is: " + day2(question));
